import DiamondGallery from "@/registry/magicui/diamond-gallery";

export default function DiamondGalleryDemo() {
    return (
        <div>
            <DiamondGallery
                images={[
                    {
                        image: '/pixel-image-demo.jpg',
                        isLink: true,
                        href: '/',
                        target: '_blank',
                        rel: 'noopener noreferrer'
                    },
                    {
                        image: '/pixel-image-demo.jpg',
                        isLink: false
                    },
                    {
                        image: '/pixel-image-demo.jpg',
                        isLink: false
                    },
                    {
                        image: '/pixel-image-demo.jpg',
                        isLink: true,
                        href: '/',
                        target: '',
                        rel: ''
                    }
                ]}
            />
        </div>
    )
}
