"use client"
import React, { useEffect, useState } from "react";
import * as LDRS from "ldrs";

type Props = {
  name: keyof typeof LDRS;
  size?: string;
  color?: string;
  speed?: string;
  stroke?: string;
  strokeLength?: string;
  loaderBgOpacity?: string;
  blurBg?: boolean;
  containerRef?: React.RefObject<any>;
};

function Loader({
  name,
  size = "40",
  color = "black",
  speed = "0.8",
  stroke,
  strokeLength,
  loaderBgOpacity,
  blurBg = true,
  containerRef,
}: Props) {
    const [isClient, setIsClient] = useState(false);

   useEffect(() => {
    setIsClient(true);
    const loader = LDRS[name];
    if (loader && (loader as any).register) {
      (loader as any).register();
    }
  }, [name]);

  if (!isClient) return null;

  const Tag =
    `l-${(name as string).replace(/([a-z0-9])([A-Z])/g, "$1-$2").toLowerCase()}` as keyof JSX.IntrinsicElements;

  const LoaderContent = (
    <div
      className={` absolute inset-0 flex items-center justify-center ${
        blurBg
          ? "bg-clip-padding backdrop-filter backdrop-blur-sm bg-opacity-10"
          : "bg-white/50 dark:bg-black/50"
      }`}
    >
      {/* <Tag size={size} color={color} speed={speed} stroke={stroke} strokeLength={strokeLength} loaderBgOpacity={loaderBgOpacity} /> */}
      {React.createElement(Tag, {
        size,
        color,
        speed,
        stroke,
        strokeLength,
        loaderBgOpacity,
      })}
    </div>
  );

  return containerRef?.current ? (
    // inside a container
    <div className="absolute z-50 top-0 w-full h-full">{LoaderContent}</div>
  ) : (
    // for whole page
    <div className="fixed inset-0 z-[51] flex items-center justify-center">
      {LoaderContent}
    </div>
  );
}

export default Loader;
