import {
  AnimatedSpan,
  Terminal,
  TypingAnimation,
} from "@/registry/magicui/terminal";

export default function TerminalDemo2() {
  return (
    <Terminal>
      <TypingAnimation delay={0}>&gt; npm install</TypingAnimation>

      <AnimatedSpan delay={1000} className="text-green-500">
        ✔ Installing packages...
      </AnimatedSpan>

      <AnimatedSpan delay={2000} className="text-green-500">
        ✔ Done!
      </AnimatedSpan>

      <TypingAnimation delay={3000} className="text-muted-foreground">
        Ready to start coding.
      </TypingAnimation>
    </Terminal>
  );
}
